import {
    SET_ROW_ACTION,
    SET_COL_ACTION,
    SET_PRO_ACTION,
    SET_DURATION_ACTION,
    TOGGLE_SETTING_ACTION,
    SELECT_CHANGE_ACTION,
    SET_TIME_ACTION,
    IS_IN_GAME_ACTION,
    RESET_GAME_ACTION,
    RIGHT_CLICK_CELL_ACTION,
    LEFT_CLICK_CELL_ACTION
} from './Action';
import {
    levels
} from './Functions';

export const initialState = {
    'level': 0,
    'row': 0,
    'col': 0,
    'proportion': 0,
    'mine': 0,
    'setTime': {
        isTime: true,
        duration: 0
    },
    'hidden': false,
    'cells': [],
    'isInGame': false,
    'firstClick': true
}

export const reducer = (state, action) => {

    console.log(action)
    switch (action.type) {
        case SET_ROW_ACTION:
            return {
                ...state,
                'row': action.payload,
                'mine': Math.floor((state.col + action.payload) / 2 * state.proportion),
                'cells': new Array(action.payload * state.col).fill({
                    opened: false,
                    flag: false,
                    isMine: false
                })
            }
        case SET_COL_ACTION:
            return {
                ...state,
                'col': action.payload,
                'mine': Math.floor((state.row + action.payload) / 2 * state.proportion),
                'cells': new Array(action.payload * state.row).fill({
                    opened: false,
                    flag: false,
                    isMine: false
                })
            }
        case SET_PRO_ACTION:
            return {
                ...state,
                'proportion': action.payload,
                'mine': Math.floor((state.col + state.row) / 2 * action.payload)
            }
        case SET_TIME_ACTION:
            return {
                ...state,
                'setTime': {
                    ...state.setTime,
                    'isTime': !state.setTime.isTime
                }
            }
        case SET_DURATION_ACTION:
            return {
                ...state,
                'setTime': {
                    ...state.setTime,
                    'duration': action.payload
                }
            }
        case SELECT_CHANGE_ACTION:
            return {
                ...state,
                ...levels[action.payload]
            }
        case TOGGLE_SETTING_ACTION:
            return {
                ...state,
                'hidden': !state.hidden
            }
        case IS_IN_GAME_ACTION:
            return {
                ...state,
                'isInGame': true,
                'hidden': true
            }
        case RESET_GAME_ACTION:
            return {
                ...state,
                ...levels[4],
                'isInGame': false
            }
        case LEFT_CLICK_CELL_ACTION:
            return {
                ...state,
                'cells': action.payload,
                'firstClick': false
            }
        case RIGHT_CLICK_CELL_ACTION:
            return {
                ...state,
            }
        default:
            throw new Error('Invalid action')
    }
}
