import Icon from '@mui/material/Icon'
import { setCells, setFlag } from './Action';
import {
    handleClickCell,
    handleToggleFlag
} from './Functions';

function Cell({
    mine,
    dispatch,
    index,
    settings
}) {

    return (
        <div
            className={`
                game-broad-cell flex-div 
                ${settings.cells[index].opened ? "open" + mine : ""}
            `}
            onClick={() => {
                const copySettings = {
                    ...settings,
                    'cells': settings.cells.map(e => ({ ...e })),
                    'setTime': {
                        ...settings.setTime
                    }
                }
                const updatedCells = handleClickCell(index, copySettings);
                if (updatedCells) {
                    dispatch(setCells(updatedCells));
                }
            }}
            onContextMenu={(e) => {
                e.preventDefault()

                const copySettings = {
                    ...settings,
                    'cells': settings.cells.map(e => ({ ...e })),
                    'setTime': {
                        ...settings.setTime
                    }
                }
                const updatedCells = handleToggleFlag(index, copySettings);
                if (updatedCells) {
                    dispatch(setFlag(updatedCells));
                }
            }}
        >
            {
                settings.gameOver ?
                    <Icon
                        sx={{
                            fontSize: '2rem',
                            color: 'var(--cl-red-flag)'
                        }}
                    >bomb</Icon>
                    :
                    (
                        settings.cells[index].flag ?
                            <Icon
                                sx={{
                                    fontSize: '2rem',
                                    color: 'var(--cl-red-flag)'
                                }}
                            >flag</Icon>
                            :
                            (mine === 0 ? "" : mine)
                    )
            }
        </div>
    );
}

export default Cell;
