import { useState, useEffect } from "react"

function Clock({ dispatch, duration }) {

    
    const [clock, setClock] = useState({
        'minute': Math.floor(duration / 60),
        'second': duration - Math.floor(duration / 60) * 60,
        'interval': null,
        'remain': duration * 1000,
        'end': Date.now() + duration * 1000
    })

    useEffect(() => {

        const endTime = Date.now() + duration * 1000;

        const interval = setInterval(() => {
            const remain = endTime - Date.now();
            const second = Math.floor(remain / 1000) % 60;
            const minute = Math.floor(remain / 60000);

            setClock(prev => ({
                ...prev,
                minute,
                second,
                remain
            }));

            // Dừng khi hết giờ
            if (remain <= 0) {
                clearInterval(interval);
                setClock(prev => ({
                    ...prev,
                    minute: 0,
                    second: 0,
                    remain: 0
                }));
            }

        }, 1000);

        // Lưu interval nếu muốn clear từ ngoài
        setClock(prev => ({
            ...prev,
            interval,
            end: endTime
        }));

        return () => clearInterval(interval);

    }, [duration])

    console.log("hello")

    return (
        <div className="minesweeper-settings-clock">
            <div className="countdown-clock flex-div">
                <div className="countdown-clock-time">
                    {clock.minute < 10 ? `0${clock.minute}` : clock.minute}
                </div>
                <div className="countdown-clock-spread">:</div>
                <div className="countdown-clock-time">
                    {clock.second < 10 ? `0${clock.second}` : clock.second}
                </div>
            </div>
        </div>
    )
}

export default Clock
