import { useEffect, useRef } from "react"

function Logger({ log }) {

    const ref = useRef(null)

    useEffect(() => {
        const logDuration = setTimeout(function(){
            ref.current.textContent = log
        }, 2000)

        return () => clearTimeout(logDuration)
    }, [log])

    return (
        <div className="logger" ref={ref}></div>
    )
}
export default Logger
