// Levels of game
export const levels = [
    {
        'level': 0,
        'row': 0,
        'col': 0,
        'proportion': 0,
        'mine': 0,
        'setTime': {
            isTime: true,
            duration: 0
        },
        'cells': []
    },
    {
        'level': 1,
        'row': 10,
        'col': 10,
        'proportion': 1.5,
        'mine': 15,
        'setTime': {
            isTime: true,
            duration: 300
        },
        'cells': new Array(100).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        })
    },
    {
        'level': 2,
        'row': 15,
        'col': 15,
        'proportion': 2.5,
        'mine': 37,
        'setTime': {
            isTime: true,
            duration: 420
        },
        'cells': new Array(225).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        })
    },
    {
        'level': 3,
        'row': 25,
        'col': 25,
        'proportion': 4,
        'mine': 100,
        'setTime': {
            isTime: true,
            duration: 600
        },
        'cells': new Array(625).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        })
    },
    {
        'level': 4,
        'row': 20,
        'col': 20,
        'proportion': 1.5,
        'mine': 30,
        'setTime': {
            isTime: true,
            duration: 600
        },
        'hidden': false,
        'cells': new Array(400).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        })
    }
]


//Custom marks of Slider component
export const marks = [
    {
        value: 1.5,
        label: <label className="easy-green">Easy</label>
    },
    {
        value: 2.5,
        label: <label className="medium-purple">Medium</label>
    },
    {
        value: 4,
        label: <label className="hard-red">Hard</label>
    }
];

//logic game functions

export function convertToMinute(seconds) {
    return `${Math.floor(seconds / 60)}m`
}

export const isMobileDevice = () => {
    return /Mobi|Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
}

//Constructor of cells
// 'cells': new Array(size).fill({
//     opened: false,
//     flag: false,
//     isMine: false,
//     mine: 0
// })

function generateMines(rowrows, cols, mines, cells) {
    const minesPosition = new Set()

    while(minesPosition.size() < mines){
        const row = Math.floor(Math.random() * rows)
        const col = Math.floor(Math.random() * cols)

        if (!minesPosition.has(`${row}, ${col}`)){
            minesPosition.add(`${row}, ${col}`)
            cells[row * cols + col] = {
                ...cells[row * cols + col],
                'isMine': true
            }
        }
    }

    return cells
}

function countMineAround(row, col, cols, cells){

    let count = 0
    for (let i = -1; i <= 1; i++) {
        for (let j = -1; j <= 1; j++) {
            if (i === 0 && j === 0) continue

            const cell = cells[(row + i) * cols + (col + j)]

            if (cell.isMine){
                count++;
            }
        }
    }

    return count
}

function revealEmptyCells(row, col, rows, cols, cells) {

    for (let i = -1; i <= 1; i++){
        for (let j = -1; j <= 1; j++){
            if (i === 0 && j === 0) continue

            if (
                ((row + i < 0) || (row + i >= rows)) &&
                ((col + j < 0) || (col + j >= cols)) &&
                !cells[(row + i) * cols + (col + j)].flag &&
                !cells[(row + i) * cols + (col + j)].isMine &&
                !cells[(row + i) * cols + (col + j)].opened
            ){
                let mines
                if (
                    (mines = countMineAround(row + i, col + j, cols, cells)) > 0
                ){
                    cells[(row + i) * cols + (col + j)].mine = mines
                    return cells
                } else {
                    return revealEmptyCells(row + i, col + j, rows, cols, cells)
                }
            }
        }
    }

}

export function handleClickCell(index, rows, cols, cells) {
    const [row, col] = [
        Math.floor(index / cols),
        index % cols
    ]
}
