// Levels of game
export const levels = [
    {
        'level': 0,
        'row': 0,
        'col': 0,
        'proportion': 0,
        'mine': 0,
        'setTime': {
            isTime: true,
            duration: 0
        },
        'cells': [],
        'firstClick': true
    },
    {
        'level': 1,
        'row': 10,
        'col': 10,
        'proportion': 1.5,
        'mine': 15,
        'setTime': {
            isTime: true,
            duration: 300
        },
        'cells': new Array(100).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        }),
        'firstClick': true
    },
    {
        'level': 2,
        'row': 15,
        'col': 15,
        'proportion': 2.5,
        'mine': 37,
        'setTime': {
            isTime: true,
            duration: 420
        },
        'cells': new Array(225).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        }),
        'firstClick': true
    },
    {
        'level': 3,
        'row': 25,
        'col': 25,
        'proportion': 4,
        'mine': 100,
        'setTime': {
            isTime: true,
            duration: 600
        },
        'cells': new Array(625).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        }),
        'firstClick': true
    },
    {
        'level': 4,
        'row': 20,
        'col': 20,
        'proportion': 1.5,
        'mine': 30,
        'setTime': {
            isTime: true,
            duration: 600
        },
        'hidden': false,
        'cells': new Array(400).fill({
            opened: false,
            flag: false,
            isMine: false,
            mine: 0
        }),
        'firstClick': true
    }
]


//Custom marks of Slider component
export const marks = [
    {
        value: 1.5,
        label: <label className="easy-green">Easy</label>
    },
    {
        value: 2.5,
        label: <label className="medium-purple">Medium</label>
    },
    {
        value: 4,
        label: <label className="hard-red">Hard</label>
    }
];

//logic game functions

export function convertToMinute(seconds) {
    return `${Math.floor(seconds / 60)}m`
}

export const isMobileDevice = () => {
    return /Mobi|Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
}

//Constructor of cells
// 'cells': new Array(size).fill({
//     opened: false,
//     flag: false,
//     isMine: false,
//     mine: 0
// })

function generateMines(initRow, initCol, rows, cols, mines, cells) {
    const minesPosition = new Set()


    for (let i = -1; i <= 1; i++) {
        for (let j = -1; j <= 1; j++) {
            minesPosition.add(`${initRow + i}, ${initCol + j}`)
        }
    }

    while (minesPosition.size < mines + 9) {
        const row = Math.floor(Math.random() * rows)
        const col = Math.floor(Math.random() * cols)

        if (!minesPosition.has(`${row}, ${col}`)) {
            minesPosition.add(`${row}, ${col}`)
            cells[row * cols + col].isMine = true
        }
    }
    return cells
}

function countMineAround(row, col, rows, cols, cells) {

    let count = 0
    for (let i = -1; i <= 1; i++) {
        for (let j = -1; j <= 1; j++) {
            if (i === 0 && j === 0) continue

            if (
                !((row + i < 0) || (row + i >= rows)) &&
                !((col + j < 0) || (col + j >= cols))
            ) {
                let cell = cells[(row + i) * cols + (col + j)]

                if (cell.isMine) {
                    count++;
                }
            }
        }
    }


    return count
}

function revealEmptyCells(row, col, rows, cols, cells) {

    function recursion(r, c) {
        if (cells[r * cols + c].isMine) return

        let mines = countMineAround(r, c, rows, cols, cells)
        cells[r * cols + c].opened = true

        if (mines > 0) {
            cells[r * cols + c].mine = mines
            return
        } else {
            for (let i = -1; i <= 1; i++) {
                for (let j = -1; j <= 1; j++) {
                    if (
                        !((r + i < 0) || (r + i >= rows)) &&
                        !((c + j < 0) || (c + j >= cols)) &&
                        !(i === 0 && j === 0)
                    ) {
                        if (!cells[(r + i) * cols + (c + j)].opened &&
                            !cells[(r + i) * cols + (c + j)].flag) 
                        {
                            recursion(r + i, c + j)
                        }
                    }
                }
            }
        }
    }
    recursion(row, col)

    return cells

}

export function handleClickCell(index, settings, copySettings) {
    if (copySettings[index].opened || copySettings[index].flag) {
        return;
    }

    const [row, col] = [
        Math.floor(index / settings.col),
        index % settings.col
    ]

    if (settings.firstClick) {

        let cells = generateMines(
            row,
            col,
            settings.row,
            settings.col,
            settings.mine,
            copySettings
        )
        cells = revealEmptyCells(
            row,
            col,
            settings.row,
            settings.col,
            cells
        )

        return cells
    } else {
        const cells = revealEmptyCells(
            row,
            col,
            settings.row,
            settings.col,
            copySettings
        )
        return cells
    }

}

export function handleToggleFlag(index, copyCells) {

    if (copyCells.cells[index].opened) return copyCells

    copyCells[index].flag = !copyCells[index].flag
    return copyCells
}
