export const marks = [
    {
        value: 1.5,
        label: <label className="slider-label">Easy</label> 
    },
    {
        value: 2.5,
        label: <label className="slider-label">Medium</label>
    },
    {
        value: 4,
        label: <label className="slider-label">Hard</label>
    }
];

export const valueTextLabel = (value) => {
    let style = '';

    if (value >= 4){ style = 'hard-red'}
    else if (value >= 2.5){ style = 'medium-purple'}
    else { style = 'easy-green'}

    return <label className={`slider-label ${style}`}>Easy</label>
}
