import { useState, useReducer } from 'react'
import OptionsBar from './OptionBar';
import SettingsBroad from './SettingsBroad';
import {
    setCol,
    setRow,
    setPro,
    setDuration,
    setHiddenSetting,
    setSelect,
    setTime
} from './Action'
import { initialState, reducer } from './Reducer'
import { 
    levels, 
    marks, 
    convertToMinute, 
    generateGameBroad,
    isMobileDevice
} from './Functions';
import "./Minesweeper.css";


function Minesweeper() {

    const [settings, setSettings] = useState({
        'level': 0,
        'row': 0,
        'col': 0,
        'proportion': 0,
        'mine': 0,
        'setTime': {
            isTime: true,
            duration: 0
        }
    });

    function handleSelectChange(event) {
        setSettings(prevState => {
            return {
                ...prevState,
                ...levels[event.target.value]
            }
        })
    }

    function handleRowChange(_, newRow) {
        setSettings(prevState => {
            return {
                ...prevState,
                'row': newRow,
                'mine': Math.floor((newRow + prevState.col) / 2 * prevState.proportion)
            }
        })
    }
    function handleColumnChange(_, newColumn) {
        setSettings(prevState => {
            return {
                ...prevState,
                'col': newColumn,
                'mine': Math.floor((newColumn + prevState.row) / 2 * prevState.proportion)
            }
        })
    }
    function handleProportionChange(_, newProportion) {
        setSettings(prevState => {
            return {
                ...prevState,
                'proportion': newProportion,
                'mine': Math.floor((prevState.row + prevState.col) / 2 * newProportion)
            }
        })
    }

    function handleSetTime() {
        setSettings(prevState => {
            return {
                ...prevState,
                'setTime': {
                    ...prevState.setTime,
                    isTime: !prevState.setTime.isTime
                }
            }
        })
    }

    function handleSetDuration(_, newDuration) {
        setSettings(prevState => {
            return {
                ...prevState,
                'setTime': {
                    ...prevState.setTime,
                    duration: newDuration
                }
            }
        })
    }

    function handleHiddenSettings() {
        setSettings(prevState => {
            return {
                ...prevState,
                'hidden': !prevState.hidden
            }
        })
    }

    //console.log(settings);
    return (
        <div className="minesweeper">
            {/* Thanh thong tin */}
            <OptionsBar 
                settings={settings}
                handleSelectChange={handleSelectChange}
                handleHiddenSettings={handleHiddenSettings}
            />
            <div className="minesweeper-broad">
                {/* Bang cau hinh game */}
                <SettingsBroad 
                    settings={settings}
                    handleRowChange={handleRowChange}
                    handleColumnChange={handleColumnChange}
                    handleProportionChange={handleProportionChange}
                    handleSetTime={handleSetTime}
                    handleSetDuration={handleSetDuration}
                    handleHiddenSettings={handleHiddenSettings}
                    marks={marks}
                    convertToMinute={convertToMinute}
                />
                
                <div className="logger"></div>
                <div className="gameOver"></div>
                {/* Bang game chinh */}
                <div 
                    className="game-broad"
                    style={
                        {
                            display: 'grid',
                            gridTemplateRows: 
                                `repeat(
                                    ${settings.row},
                                    ${isMobileDevice() ? 3 : 4}rem
                                )`,
                            gridTemplateColumns:
                                `repeat(
                                    ${settings.col},
                                    ${isMobileDevice() ? 3 : 4}rem
                                )`,
                        }
                    }
                >
                    {
                        generateGameBroad(
                            settings.row,
                            settings.col
                        )
                    }
                </div>
            </div>
        </div>
    )
}

export default Minesweeper
