function Cell ({ 
    mine,
    dispatch,
    setCells,
    handleClickCell,
    index,
    settings
}) {

    return (
        <div 
            className={`
                game-broad-cell flex-div 
                ${settings.cells[index].opened}
            `}
            onClick={() => {
                const copyCells = settings.cells.map(cell => ({ ...cell }))
                const updatedCells = handleClickCell(index, settings, copyCells);
                if (updatedCells) {
                    dispatch(setCells(updatedCells));
                }
            }}
        >
            {mine}
        </div>
    );
}

export default Cell;
