import Icon from '@mui/material/Ico'
import { setCells, setFlag } from './Action';
import {
    handleClickCell,
    handleToggleFlag
} from './Functions';

function Cell({
    mine,
    dispatch,
    index,
    settings
}) {

    return (
        <div
            className={`
                game-broad-cell flex-div 
                ${settings.cells[index].opened ? "open" + mine : ""}
            `}
            onClick={() => {
                const copyCells = settings.cells.map(cell => ({ ...cell }))
                const updatedCells = handleClickCell(index, settings, copyCells);
                if (updatedCells) {
                    dispatch(setCells(updatedCells));
                }
            }}
            onContextMenu={(e) => {
                e.preventDefault()

                const copyCells = settings.cells.map(cell => ({ ...cell }))
                const updatedCells = handleToggleFlag(index, copyCells);
                if (updatedCells) {
                    dispatch(setFlag(updatedCells));
                }
            }}
        >
            {mine === 0 ? "" : mine}
            {
                settings.cells[index].flag &&
                <Icon>flag</Icon>
            }
        </div>
    );
}

export default Cell;
