import Icon from '@mui/material/Icon'
import { setCells, setFlag } from './Action'
import { handleClickCell, handleToggleFlag } from './Functions'

function Tool({
    dispatch,
    toolDisplay,
    setToolDisplay
}) {

    return (
        <div className="tool" style={toolDisplay.style}>
            <div className="tool-cell"></div>
            <div 
                className="tool-cell tool-cell-open flex-div"
                onClick={() => 
dispatch(setCells(handleClickCell(toolDisplay.index, toolDisplay.settings)))
                }
            >
                <Icon className="tool-btn open">fullscreen</Icon>
            </div>
            <div 
                className="tool-cell tool-cell-close flex-div"
                onClick={() => setToolDisplay(prev => {
                    return {
                        ...prev,
                        'style': {
                            ...prev.style,
                            display: 'none'
                        }
                    }
                })}
            >
                <Icon className="tool-btn close">close</Icon>
            </div>
            <div 
                className="tool-cell tool-cell-flag flex-div"
                onClick={() => handleToggleFlag(toolDisplay.index, toolDisplay.settings)}
            >
                <Icon className="tool-btn flag">flag</Icon>
            </div>
        </div>
    )
}

export default Tool
