import useGameSettings from "./useGameSettings";

const { setSettings } = use

//Custom marks of Slider component
export const marks = [
    {
        value: 1.5,
        label: <label className="easy-green">Easy</label>
    },
    {
        value: 2.5,
        label: <label className="medium-purple">Medium</label>
    },
    {
        value: 4,
        label: <label className="hard-red">Hard</label>
    }
];

//handle change input and state
// {
//     'level': 0,
//     'row': 0,
//     'col': 0,
//     'proportion': 0,
//     'mine': 0,
//     'setTime': {
//         isTime: true,
//         duration: 600
//     }
// }
export function handleSelectChange(event) {
    setSettings(prevState => {
        return {
            ...prevState,
            'level': event.target.value
        }
    })
}

export function handleRowChange(_, newRow) {
    setSettings(prevState => {
        return {
            ...prevState,
            'row': newRow,
            'mine': Math.floor((newRow * prevState.col) / 2) * prevState.proportion
        }
    })
}
export function handleColumnChange(_, newColumn) {
    setSettings(prevState => {
        return {
            ...prevState,
            'col': newColumn,
            'mine': Math.floor((newColumn * prevState.row) / 2) * prevState.proportion
        }
    })
}
export function handleProportionChange(_, newProportion) {
    setSettings(prevState => {
        return {
            ...prevState,
            'proportion': newProportion,
            'mine': Math.floor((prevState.row * prevState.col) / 2) * newProportion
        }
    })
}
export function handleChange(_, newRow) {
    setSettings(prevState => {
        return {
            ...prevState,
            'row': newRow
        }
    })
}
