import { useState } from 'react'
import Icon from '@mui/material/Icon';
import "./Minesweeper.css";
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import { styled } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import Slider from '@mui/material/Slider';
import { 
    levels, 
    marks, 
    convertToMinute, 
    generateGameBroad,
    isMobileDevice
} from './functions';

const BootstrapInput = styled(InputBase)(() => ({
    '& .MuiInputBase-input': {
        borderRadius: 4,
        position: 'relative',
        border: '1px solid var(--cl-gray)',
        padding: '10px 26px 10px 12px',
        fontSize: '1.4rem',
        fontWeight: 600,

        '&:focus': {
            borderColor: 'var(--cl-primary-purple)'
        }
    }
}
));

function Minesweeper() {

    const [settings, setSettings] = useState({
        'level': 0,
        'row': 0,
        'col': 0,
        'proportion': 0,
        'mine': 0,
        'setTime': {
            isTime: true,
            duration: 0
        },
        'hidden': false 
    });

    function handleSelectChange(event) {
        setSettings(prevState => {
            return {
                ...prevState,
                ...levels[event.target.value]
            }
        })
    }

    function handleRowChange(_, newRow) {
        setSettings(prevState => {
            return {
                ...prevState,
                'row': newRow,
                'mine': Math.floor((newRow + prevState.col) / 2 * prevState.proportion)
            }
        })
    }
    function handleColumnChange(_, newColumn) {
        setSettings(prevState => {
            return {
                ...prevState,
                'col': newColumn,
                'mine': Math.floor((newColumn + prevState.row) / 2 * prevState.proportion)
            }
        })
    }
    function handleProportionChange(_, newProportion) {
        setSettings(prevState => {
            return {
                ...prevState,
                'proportion': newProportion,
                'mine': Math.floor((prevState.row + prevState.col) / 2 * newProportion)
            }
        })
    }

    function handleSetTime() {
        setSettings(prevState => {
            return {
                ...prevState,
                'setTime': {
                    ...prevState.setTime,
                    isTime: !prevState.setTime.isTime
                }
            }
        })
    }

    function handleSetDuration(_, newDuration) {
        setSettings(prevState => {
            return {
                ...prevState,
                'setTime': {
                    ...prevState.setTime,
                    duration: newDuration
                }
            }
        })
    }

    function handleHiddenSettings() {
        setSettings(prevState => {
            return {
                ...prevState,
                display: !prevState.display
            }
        })
    }

    //console.log(settings);
    return (

        <div className="minesweeper">
            {/* Thanh thong tin */}
            <div className="minesweeper-settings flex-div">
                <Select
                    className="minesweeper-settings-level"
                    labelId="settings-level"
                    value={settings.level}
                    input={<BootstrapInput />}
                    onChange={handleSelectChange}

                >

                    <MenuItem value={0} sx={{ fontSize: 16 }}>
                        <em>--Level--</em>
                    </MenuItem>
                    <MenuItem value={1} sx={{ fontSize: 16 }}>Easy</MenuItem>
                    <MenuItem value={2} sx={{ fontSize: 16 }}>Medium</MenuItem>
                    <MenuItem value={3} sx={{ fontSize: 16 }}>Hard</MenuItem>
                    <MenuItem value={4} sx={{ fontSize: 16 }}>Custom</MenuItem>
                </Select>
                <div className="minesweeper-settings-flag flex-div">
                    <Icon sx={{
                        fontSize: '3rem',
                        color: 'var(--cl-red-flag)'
                    }}>flag</Icon>
                    <p>{settings.mine}</p>
                </div>
                <div className="minesweeper-settings-clock">
                    <div className="countdown-clock flex-div">
                        <div className="countdown-clock-time">10</div>
                        <div className="countdown-clock-spread">:</div>
                        <div className="countdown-clock-time">00</div>
                    </div>
                </div>
            </div>
            <div className="minesweeper-broad">
                {/* Bang cau hinh game */}
                <div 
                    className={`
                        customize-broad 
                        ${
                            settings.level === 4 && !settings.hidden ? 
                            "" : "customize-broad__hidden"
                        }
                    `}
                >
                    <div className="customize-container">
                        <div className="customize-field">
                            <p className="customize-name">Row</p>
                            <p className="customize-data">{settings.row}</p>
                        </div>
                        <Slider
                            className="range-allow"
                            valueLabelDisplay="auto"
                            defaultValue={20}
                            min={10}
                            max={30}
                            value={settings.row}
                            onChange={handleRowChange}
                        />
                    </div>
                    <div className="customize-container">
                        <div className="customize-field">
                            <p className="customize-name">Column</p>
                            <p className="customize-data">{settings.col}</p>
                        </div>
                        <Slider
                            className="range-allow"
                            valueLabelDisplay="auto"
                            defaultValue={20}
                            min={10}
                            max={30}
                            value={settings.col}
                            onChange={handleColumnChange}
                        />
                    </div>
                    <div className="customize-container">
                        <div className="customize-field">
                            <p className="customize-name">Proportion</p>
                            <p className="customize-data">{settings.proportion}</p>
                        </div>                        <Slider
                            className="range-allow"
                            valueLabelDisplay="auto"
                            defaultValue={1.5}
                            step={0.5}
                            min={1.5}
                            max={4.5}
                            marks={marks}
                            sx={{
                                '& .MuiSlider-markLabel': {
                                    color: 'var(--cl-primary-purple)',
                                    fontSize: '1.2rem',
                                    fontWeight: 600,
                                    transform: 'translateY(-.2rem)'
                                }
                            }}
                            value={settings.proportion}
                            onChange={handleProportionChange}
                        />
                    </div>
                    <div className="customize-container">
                        <div className="customize-field">
                            <p className="customize-name">Mines</p>
                            <p className="customize-data">{settings.mine}</p>
                        </div>
                        <Slider
                            className="range-allow"
                            valueLabelDisplay="auto"
                            value={settings.mine}
                            min={15}
                            max={135}
                            disabled
                        />
                    </div>
                    <div className="setting-time flex-div">
                        <input
                            type="checkbox"
                            id="setTime"
                            checked={settings.setTime.isTime}
                            onChange={handleSetTime}
                        />
                        <div className="setTime">set time</div>
                    </div>
                    {
                        settings.setTime.isTime &&
                        <div className="customize-container">
                            <div className="customize-field">
                                <p className="customize-name">Time (s)</p>
                                <p className="customize-data">
                                    {`${settings.setTime.duration}s ~ ${
                                        convertToMinute(settings.setTime.duration)
                                    }`}
                                </p>
                            </div>
                            <Slider
                                className="range-allow"
                                valueLabelDisplay="auto"
                                step={60}
                                min={300}
                                max={900}
                                value={settings.setTime.duration}
                                onChange={handleSetDuration}
                                marks
                            />
                        </div>
                    }
                    <button 
                        className="confirm-customize"
                        
                    >
                        Ok
                    </button>
                </div>
                
                <div className="logger"></div>
                <div className="gameOver"></div>
                {/* Bang game chinh */}
                <div 
                    className="game-broad"
                    style={
                        {
                            display: 'grid',
                            gridTemplateRows: 
                                `repeat(
                                    ${settings.row},
                                    ${isMobileDevice() ? 3 : 4}rem
                                )`,
                            gridTemplateColumns:
                                `repeat(
                                    ${settings.col},
                                    ${isMobileDevice() ? 3 : 4}rem
                                )`,
                        }
                    }
                >
                    {
                        generateGameBroad(
                            settings.row,
                            settings.col
                        )
                    }
                </div>
            </div>
        </div>
    )
}

export default Minesweeper
