import Icon from '@mui/material/Icon'
import CustomTool from './CustomTool';
import { setCells, setFlag } from './Action';
import {
    handleClickCell,
    handleToggleFlag,
    isMobileDevice
} from './Functions';

function Cell({
    mine,
    dispatch,
    index,
    settings
}) {

    const [_, setToolDisplay] = CustomTool()

    const copySettings = {
        ...settings,
        'cells': settings.cells.map(e => ({ ...e })),
        'setTime': {
            ...settings.setTime
        }
    }

    function leftClickForDesktop() {
        const updatedCells = handleClickCell(index, copySettings);
        if (updatedCells) {
            dispatch(setCells(updatedCells));
        }
    }

    function rightClickForDesktop() {
        const updatedCells = handleToggleFlag(index, copySettings);
        if (updatedCells) {
            dispatch(setFlag(updatedCells));
        }
    }

    return (
        <div
            className={`
                game-broad-cell flex-div 
                ${settings.cells[index].opened ? "open" + mine : ""}
            `}
            onClick={
                (e) => {
                    const rect = e.target.getBoundingClientRect();
                    console.log()
                    setToolDisplay(prev => ({
                        ...prev,
                        display: 'grid',
                        top: rect.top,
                        left: rect.left
                    }));
                }
            }
            onContextMenu={(e) => {
                e.preventDefault()
                rightClickForDesktop()
            }}
        >
            {
                settings.gameOver && settings.cells[index].isMine ?
                    <Icon
                        sx={{
                            fontSize: '2rem',
                            color: 'var(--cl-primary-yellow)'
                        }}
                    >donut_large</Icon>
                    :
                    (
                        settings.cells[index].flag ?
                            <Icon
                                sx={{
                                    fontSize: '2rem',
                                    color: 'var(--cl-red-flag)'
                                }}
                            >flag</Icon>
                            :
                            (mine === 0 ? "" : mine)
                    )
            }
        </div>
    );
}

export default Cell;
