import {
    SET_ROW_ACTION,
    SET_COL_ACTION,
    SET_PRO_ACTION,
    SET_DURATION_ACTION,
    HIDDEN_SETTING_ACTION,
    SELECT_CHANGE_ACTION,
    SET_TIME_ACTION
} from './Action';
import { levels } from './Functions';

export const initialState = {
    'level': 0,
    'row': 0,
    'col': 0,
    'proportion': 0,
    'mine': 0,
    'setTime': {
        isTime: true,
        duration: 0
    }
}

export const reducer = (state, action) => {
    console.log(action)

    switch(action) {
        case action.type === SET_ROW_ACTION:
            return {
                ...state,
                'row': action.payload,
                'mine': Math.floor((state.col + action.payload) / 2 * state.proportion)
            }
        case action.type === SET_COL_ACTION:
            return {
                ...state,
                'col': action.payload,
                'mine': Math.floor((state.row + action.payload) / 2 * state.proportion)
            }
        case action.type === SET_PRO_ACTION:
            return {
                ...state,
                'proportion': action.payload,
                'mine': Math.floor((state.col + state.row) / 2 * action.payload)
            }
        case action.type === SET_TIME_ACTION:
            return {
                ...state,
                'setTime': {
                    ...state.setTime,
                    'isTime': !state.setTime.isTime
                }
            }
        case action.type === SET_DURATION_ACTION:
            return {
                ...state,
                'setTime': {
                    ...state.setTime,
                    'duration': action.payload                
                }
            }
        case action.type === SELECT_CHANGE_ACTION:
            return {
                ...state,
                ...levels[action.payload]
            }
        case action.type === HIDDEN_SETTING_ACTION:
            return {
                ...state,
                'hidden': !state.hidden
            }
        default:
            throw new Error('Invalid action')
    }
}

console.log(SET_ROW_ACTION,
    SET_COL_ACTION,
    SET_PRO_ACTION,
    SET_DURATION_ACTION,
    HIDDEN_SETTING_ACTION,
    SELECT_CHANGE_ACTION,
    SET_TIME_ACTION)
