import { useState } from 'react';
import { Link } from 'react-router-dom';
import Icon from '@mui/material/Icon'
import './FormLogin.css';

function FormLogin ({ open }) {

    const [userInfo, setUserInfo] = useState({
        'username': '',
        'password': ''
    })
    const [showPassword, setShowPassword] = useState(false);

    function handleGetUsername(event){
        setUserInfo(prevState => {
            return {
                ...prevState,
                'username': event.target.value
            }
        })
    }
    function handleGetUsername(event){
        setUserInfo(prevState => {
            return {
                ...prevState,
                'username': event.target.value
            }
        })
    }

    return (
        <div className={`dropdown position-right ${open ? "" : "close"}`}>
            <form action="http://localhost:3000/login" method="post" className="loginForm flex-div">
                <div className="field-container flex-div">
                    <input type="text" name="username" className="input-field text-type"/>
                    <div className="name-field">Username/Email</div>
                </div>
                <div className="field-container flex-div">
                    <input type={`${showPassword ? "text" : "password"}`} name="password" className="input-field password-type" />
                    <Icon 
                        className="display-password"
                        onClick={() => setShowPassword(prevState => !prevState)}
                    >
                        visibility{`${showPassword ? "_off" : ""}`}
                    </Icon>
                    <div className="name-field">Password</div>
                </div>
                <div className="additionalLinks-before"></div>
                <div className="additionalLinks flex-div">
                    <div className="additionalLinks-left flex-div">
                        <input type="checkbox" name="record" id="record" />
                        <label htmlFor="record">Remember me</label>
                    </div>
                    <div className="additionalLinks-right">
                        <Link to="/recovery-password" className="additionalLinks-right-anchor">Forget password?</Link>
                    </div>
                </div>
                <button type="submit" className="submitBtn">Log in</button>
            </form>
            <div className="different-method flex-div">
                Login by Google
            </div>
            <div className="different-method flex-div">
                Login by Facebook
            </div>
        </div>
    );
}

export default FormLogin
