    setCol,
    setRow,
    setPro,
    setDuration,
    setHiddenSetting,
    setSelect,
    setTime
} from './Action'
import { initialState, reducer } from './Reducer'
import { 
    levels, 
    marks, 
    convertToMinute, 
    generateGameBroad,
    isMobileDevice
} from './Functions';
import "./Minesweeper.css";


function Minesweeper() {

    const [settings, dispatch] = useReducer(reducer, initialState)

