    // function handleHiddenSettings() {
    //     setSettings(prevState => {
    //         return {
    //             ...prevState,
    //             'hidden': !prevState.hidden
    //         }
    //     })
    // }

    //console.log(settings);
    return (
        <div className="minesweeper">
            {/* Thanh thong tin */}
            <OptionsBar 
                settings={settings}
                handleSelectChange={handleSelectChange}
                handleHiddenSettings={handleHiddenSettings}
            />
            <div className="minesweeper-broad">
                {/* Bang cau hinh game */}
                <SettingsBroad 
                    settings={settings}
                    handleRowChange={handleRowChange}
                    handleColumnChange={handleColumnChange}
                    handleProportionChange={handleProportionChange}
                    handleSetTime={handleSetTime}
                    handleSetDuration={handleSetDuration}
                    handleHiddenSettings={handleHiddenSettings}
                    marks={marks}
                    convertToMinute={convertToMinute}
                />
                
                <div className="logger"></div>
                <div className="gameOver"></div>
                {/* Bang game chinh */}
                <div 
                    className="game-broad"
                    style={
                        {
                            display: 'grid',
                            gridTemplateRows: 
                                `repeat(
                                    ${settings.row},
                                    ${isMobileDevice() ? 3 : 4}rem
                                )`,
                            gridTemplateColumns:
                                `repeat(
                                    ${settings.col},
                                    ${isMobileDevice() ? 3 : 4}rem
                                )`,
                        }
                    }
                >
                    {
                        generateGameBroad(
                            settings.row,
                            settings.col
                        )
                    }
                </div>
            </div>
        </div>
    )
}

export default Minesweeper
